import React, { useState } from "react";
import { Grid } from "@mui/material";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const Day = () => {
    const initialItems = [
        { id: 'item-1', content: 'Item 1' },
        { id: 'item-2', content: 'Item 2' },
        // Add more items as needed
    ];

    const [items, setItems] = useState(initialItems);

    return (
        <Grid container gap={8} marginTop={'1em'} justifyContent={'center'}>
            <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={550}>
                <DragDropContext
                    onDragEnd={(result) => {
                        const { source, destination } = result;

                        // Check if the drag is within valid indices
                        if (!destination) return;

                        // Update the state based on the drag result
                        const newItems = Array.from(items);
                        const [movedItem] = newItems.splice(source.index, 1);
                        newItems.splice(destination.index, 0, movedItem);

                        setItems(newItems);
                    }}
                >
                    <Droppable droppableId="droppable">
                        {(provided) => (
                            <ul {...provided.droppableProps} ref={provided.innerRef}>
                                {items.map((item, index) => (
                                    <Draggable key={item.id} draggableId={item.id} index={index}>
                                        {(provided) => {
                                            console.log(provided); // Log provided object to the console

                                            return (
                                                <li
                                                    ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                    style={{
                                                        listStyleType: 'none',
                                                        padding: '8px',
                                                        margin: '8px',
                                                        border: '1px solid #ddd',
                                                        backgroundColor: '#fff'
                                                    }}
                                                >
                                                    {item.content}
                                                </li>
                                            );
                                        }}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </ul>
                        )}
                    </Droppable>
                </DragDropContext>
            </Grid>
            <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={550}>

            </Grid>
            <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={550}>

            </Grid>
        </Grid>
    );
}

export default Day;
