import React, { useState } from "react";
import { Grid, TextField } from "@mui/material";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const Day = () => {
  const initialItems = [
    { id: 'item-1', content: '1' },
    { id: 'item-2', content: '2' },
    { id: 'item-3', content: '3' },
    { id: 'item-4', content: '4' },
  ];

  const [items, setItems] = useState(initialItems);

  const onDragEnd = (result) => {
    const {destination,source} = result;
    if (!destination) return;

    console.log(result)

    if (destination.droppableId == source.droppableId && destination.index == source.index) return

    if (destination.droppableId == 'droppable2') {
        sourceIndex = [source.index]
        sourceIndex.splice(source.index, 1)
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
        <Grid container gap={8} marginTop={'1em'} justifyContent={'center'}>
            <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={550}>
                <Droppable droppableId="droppable1">
                    {(provided) => (
                    <ul
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                        style={{
                            listStyleType: 'none',
                            padding: '8px',
                            margin: '8px',
                            minHeight:'200px'
                        }}
                    >
                        {items.map((item, index) => (
                        <Draggable key={item.id} draggableId={item.id} index={index}>
                            {(provided) => (
                            <li
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                style={{
                                    listStyleType: 'none',
                                    padding: '8px',
                                    margin: '8px',
                                    backgroundColor: '#262626',
                                    borderRadius: '1em',
                                    ...provided.draggableProps.style
                                }}
                            >
                                <p style={{color: 'white'}}>Test {item.content}</p>
                            </li>
                            )}
                        </Draggable>
                        ))}
                        {provided.placeholder}
                    </ul>
                    )}
                </Droppable>
            </Grid>
            <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>
                <Droppable droppableId="droppable2">
                    {(provided) => (
                    <ul
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                        style={{
                            listStyleType: 'none',
                            padding: '8px',
                            margin: '8px',
                            minHeight:'200px'
                        }}
                    >
                        {provided.placeholder}
                    </ul>
                    )}
                </Droppable>
            </Grid>
        <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>
            These
        </Grid>
    </Grid>
</DragDropContext>
  );
}

export default Day;
