import React from "react";
import { Button, Grid } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { useDispatch } from "react-redux";
import { setShowModal } from "../../Reducers/DayReducer";
import { useSelector } from "react-redux";
import DayModal from "./DayModal";

const Day = () => {
    const dispatch = useDispatch();
    const showModal = useSelector(state => state.dayReducer.showModal);
    const priorityLevel = useSelector(state => state.dayReducer.priorityLevel);
    const titleFieldValue = useSelector(state => state.dayReducer.titleFieldValue);
    const descriptionFieldValue = useSelector(state => state.dayReducer.descriptionFieldValue);
    const tasks = useSelector(state => state.dayReducer.tasks)

    const items = [
        { id: '1', content: '11111111111111', index: 1 }, { id: '2', content: '2222222222222', index: 2  }, { id: '3', content: '3333333333333', index: 3  }
    ]

    console.log(tasks);
    console.log(tasks.findIndex(item => item.titleFieldValue == 'Testing Title'));

    const openModal = () => {
        dispatch(setShowModal(true));
    };

    const onDragEnd = (result) => {
        
    };

    return (
        <>
            <Button
                variant='contained'
                sx={{
                    borderRadius: 5,
                    position: 'absolute',
                    top: '5%',
                    right: '10%'
                }}
                onClick={() => {openModal()}}
            >
                <AddIcon />
            </Button>
            <DayModal 
                title={'Add new task'}
                showModal={showModal}
            />
            <DragDropContext onDragEnd={onDragEnd}>
                <Grid container columnGap={8} rowGap={2} marginTop={'1em'} justifyContent={'center'}>
                    <Grid item xs={3.33} marginLeft={'1em'} fontSize={35}>
                        Not Started
                    </Grid>
                    <Grid item xs={3.33} fontSize={35}>
                        In Progress
                    </Grid>
                    <Grid item xs={3.33} fontSize={35}>
                        Completed
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={500}>
                        <Droppable droppableId="droppable1">
                            {(provided) => (
                            <ul
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                                style={{
                                    listStyleType: 'none',
                                    padding: '8px',
                                    margin: '8px',
                                    minHeight:'200px'
                                }}
                            >
                                {tasks.map((item, index) => (
                                        <Draggable key={item.index} draggableId={String(item.index)} index={index}>
                                            {(provided, snapshot) => (
                                                <div
                                                    ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                >
                                                    {item.content}
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                {provided.placeholder}
                            </ul>
                            )}
                        </Droppable>
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>
                        <Droppable droppableId="droppable2">
                            {(provided) => (
                            <ul
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                                style={{
                                    listStyleType: 'none',
                                    padding: '8px',
                                    margin: '8px',
                                    minHeight:'200px'
                                }}
                            >
                                {provided.placeholder}
                            </ul>
                            )}
                        </Droppable>
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>

                    </Grid>
                </Grid>
            </DragDropContext>
        </>
    );
}

export default Day;
