import { createSlice } from '@reduxjs/toolkit';

const addTaskSlice = createSlice({
  name: 'addTask',
  initialState: {
    showModal: false,
    priorityLevel: 0,
    titleFieldValue: '',
    descriptionFieldValue: '',
    inCompleteTasks: [],
    activeTasks: []
  },
  reducers: {
    setShowModal: (state, action) => {
      state.showModal = action.payload;
    },
    setPriorityLevel: (state, action) => {
      state.priorityLevel = action.payload;
    },
    setTitleFieldValue: (state, action) => {
      state.titleFieldValue = action.payload;
    },
    setDescriptionFieldValue: (state, action) => {
      state.descriptionFieldValue = action.payload;
    },
    setNewTask: (state, action) => {
      state.inCompleteTasks.push(action.payload);
    },
    moveTask: (state, action) => {
      console.log(action.payload)
      console.log(state.inCompleteTasks.filter((index) => index !== action.payload.source.index))
      const movedItem = state.inCompleteTasks[action.payload.source.index];
      console.log('movedItems >>', movedItem)
      const updatedItems = state.inCompleteTasks.filter((_, index) => index !== action.payload.source.index);
      console.log('updatedItems >>', updatedItems)
      const updatedSecondDroppableItems = [...state.inCompleteTasks.slice(0, action.payload.destination.index), movedItem, ...state.inCompleteTasks.slice(action.payload.destination.index)];
      console.log('updatedSecondDroppableItems >>', updatedSecondDroppableItems)
      state.inCompleteTasks = updatedItems
      state.activeTasks = updatedSecondDroppableItems
    }
  },
});

export const {
  setShowModal,
  setPriorityLevel,
  setTitleFieldValue,
  setDescriptionFieldValue,
  setNewTask,
  moveTask
} = addTaskSlice.actions;

export default addTaskSlice.reducer;
