import React from "react";
import { Button, Grid, createTheme } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import { useDispatch } from "react-redux";
import { setShowModal } from "../../Reducers/DayReducer";
import { useSelector } from "react-redux";
import DayModal from "./DayModal";

const Day = () => {
    const dispatch = useDispatch();
    const showModal = useSelector(state => state.dayReducer.showModal);

    const AddTask = () => {
        dispatch(setShowModal(true));
    };

    const closeModal = () => {
        dispatch(setShowModal(false));
    }

    const onDragEnd = (result) => {
        
    };

    return (
        <>
            <Button
                variant='contained'
                sx={{
                    borderRadius: 5,
                    position: 'absolute',
                    top: '5%',
                    right: '10%'
                }}
                onClick={() => {AddTask()}}
            >
                <AddIcon />
            </Button>
            <DayModal 
                title={'Add new task'}
                showModal={showModal}
                
            />
            <DragDropContext onDragEnd={onDragEnd}>
                <Grid container columnGap={8} rowGap={2} marginTop={'1em'} justifyContent={'center'}>
                    <Grid item xs={3.33} marginLeft={'1em'} fontSize={35}>
                        Not Started
                    </Grid>
                    <Grid item xs={3.33} fontSize={35}>
                        In Progress
                    </Grid>
                    <Grid item xs={3.33} fontSize={35}>
                        Completed
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={500}>
                        <Droppable droppableId="droppable1">
                            {(provided) => (
                            <ul
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                                style={{
                                    listStyleType: 'none',
                                    padding: '8px',
                                    margin: '8px',
                                    minHeight:'200px'
                                }}
                            >
                                {provided.placeholder}
                            </ul>
                            )}
                        </Droppable>
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>
                        <Droppable droppableId="droppable2">
                            {(provided) => (
                            <ul
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                                style={{
                                    listStyleType: 'none',
                                    padding: '8px',
                                    margin: '8px',
                                    minHeight:'200px'
                                }}
                            >
                                {provided.placeholder}
                            </ul>
                            )}
                        </Droppable>
                    </Grid>
                    <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>

                    </Grid>
                </Grid>
            </DragDropContext>
        </>
    );
}

export default Day;
