import { LeaderboardTwoTone } from '@mui/icons-material';
import { createSlice } from '@reduxjs/toolkit';

const addTaskSlice = createSlice({
  name: 'addTask',
  initialState: {
    showModal: false,
    priorityLevel: 0,
    titleFieldValue: '',
    descriptionFieldValue: '',
    allTasks: [],
    incompleteTasks: [],
    activeTasks: [],
    finishedTasks: []
  },
  reducers: {
    setShowModal: (state, action) => {
      state.showModal = action.payload;
    },
    setPriorityLevel: (state, action) => {
      state.priorityLevel = action.payload;
    },
    setTitleFieldValue: (state, action) => {
      state.titleFieldValue = action.payload;
    },
    setDescriptionFieldValue: (state, action) => {
      state.descriptionFieldValue = action.payload;
    },
    setNewTask: (state, action) => {
      state.incompleteTasks.push(action.payload);
      state.allTasks.push(action.payload);
    },
    moveTask: (state, action) => {
      console.log(action.payload)

      let movedItem;

      switch (action.payload.start) {
        case 'incompleteTasks':
          console.log(state.incomplete[action.payload.source.index])
          movedItem = state.incomplete[action.payload.source.index];
          state.incompleteTasks = state.incomplete.filter((_, index) => index !== action.payload.source.index);
        case 'activeTasks':
          movedItem = state.active[action.payload.source.index];
          state.activeTasks = state.active.filter((_, index) => index !== action.payload.source.index);
        case 'finishedTasks':
          movedItem = state.finished[action.payload.source.index];
          state.finishedTasks = state.finished.filter((_, index) => index !== action.payload.source.index);
      }

      console.log(movedItem)

      switch (action.payload.end) {
        case 'incompleteTasks':
          state.incompleteTasks.push(movedItem)
        case 'activeTasks':
          state.activeTasks.push(movedItem)
        case 'finishedTasks':
          state.finishedTasks.push(movedItem)
      }
    }
  },
});

export const {
  setShowModal,
  setPriorityLevel,
  setTitleFieldValue,
  setDescriptionFieldValue,
  setNewTask,
  moveTask
} = addTaskSlice.actions;

export default addTaskSlice.reducer;
