import React, { useState } from "react";
import { Grid, Tab, Tabs, Paper } from "@mui/material";

const Header = () => {
    let path = document.location.pathname;
    const [value, setValue] = React.useState(path);

    const handleChange = (event, path) => {
        setValue(path);
        document.location.pathname = `${path}`
    };
    return (
        <Grid container spacing={1}>
            <Grid item xs={12}>
            <Paper style={{ backgroundColor: '#262626', padding: '20px' }}>
                <Tabs
                    value={value}
                    onChange={handleChange}
                    textColor="inherit"
                    sx={{ 
                        "& .MuiTabs-indicator": { backgroundColor: "white" } 
                    }}
                    indicatorColor="primary"
                    >
                        <Tab value='' label='Dashboard' sx={{ color: 'white' }} />
                        <Tab value='day' label='Day' sx={{ color: 'white' }} />
                        <Tab value='week'label='Week' sx={{ color: 'white' }} />
                </Tabs>
            </Paper>
            </Grid>
        </Grid>
    )
}

export default Header;
