import { LeaderboardTwoTone } from '@mui/icons-material';
import { createSlice } from '@reduxjs/toolkit';

const addTaskSlice = createSlice({
  name: 'addTask',
  initialState: {
    showModal: false,
    priorityLevel: 0,
    titleFieldValue: '',
    descriptionFieldValue: '',
    allTasks: [],
    incompleteTasks: [],
    activeTasks: [],
    finishedTasks: []
  },
  reducers: {
    setShowModal: (state, action) => {
      state.showModal = action.payload;
    },
    setPriorityLevel: (state, action) => {
      state.priorityLevel = action.payload;
    },
    setTitleFieldValue: (state, action) => {
      state.titleFieldValue = action.payload;
    },
    setDescriptionFieldValue: (state, action) => {
      state.descriptionFieldValue = action.payload;
    },
    setNewTask: (state, action) => {
      state.incompleteTasks.push(action.payload);
      state.allTasks.push(action.payload);
    },
    moveTask: (state, action) => {
      let movedItem;

      switch (action.payload.source.droppableId) {
        case 'incomplete':
          movedItem = state.incompleteTasks[action.payload.source.index];
          state.incompleteTasks = state.incompleteTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'active':
          movedItem = state.activeTasks[action.payload.source.index];
          state.activeTasks = state.activeTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'finished':
          movedItem = state.finishedTasks[action.payload.source.index];
          state.finishedTasks = state.finishedTasks.filter((_, index) => index !== action.payload.source.index);
          break;
      }

      switch (action.payload.destination.droppableId) {
        case 'incomplete':
          state.incompleteTasks.push(movedItem)
          break;
        case 'active':
          state.activeTasks.push(movedItem)
          break;
        case 'finished':
          state.finishedTasks.push(movedItem)
          break;
      }
    },
    reorderTasks: (state, action) => {
      switch (action.payload.source.droppableId) {
        case 'incomplete':
          const movedItem = state.incompleteTasks.filter((_, index) => index == action.payload.source.index)
          console.log(movedItem)
          const updatedList = state.incompleteTasks.filter((_, index) => index != action.payload.source.index)
          console.log(updatedList);

          console.log(updatedList.slice(0, action.payload.destination.index));
          console.log(updatedList.slice(action.payload.destination.index));

          state.incompleteTasks.splice(action.payload.destination.index, 0, movedItem)

          // newList.concat(updatedList.slice(0, action.payload.destination.index)).concat(movedItem).concat(updatedList.slice(action.payload.destination.index))

          // .slice(0, action.payload.destination.index)
          // .concat(movedItem)
          // .concat(state.incompleteTasks.slice(action.payload.destination.index + 1));
          console.log(state.incompleteTasks)
          break;
        case 'active':
          
          break;
        case 'finished':
          
          break;
      }
    }
  },
});

export const {
  setShowModal,
  setPriorityLevel,
  setTitleFieldValue,
  setDescriptionFieldValue,
  setNewTask,
  moveTask,
  reorderTasks
} = addTaskSlice.actions;

export default addTaskSlice.reducer;
