import { Grid, ImageList, ImageListItem } from "@mui/material";
import React from "react";
import { useSelector } from "react-redux";

const Dashboard = () => {
    const taskCount = JSON.parse(localStorage.getItem('allTasks')).length;

    const textStyle = {
        titles: {
            color: '#f5f5f5',
            display: 'flex',
            justifyContent: 'center',
        },
        values: {
            margin: 0,
            color: '#f5f5f5',
            display: 'flex',
            justifyContent: 'center',
            fontSize: 45
        }
    }

    return (
        <Grid container columnGap={3} rowGap={2} marginTop={'1em'} justifyContent={'center'}>
            <Grid
                container
                item
                border={1}
                borderRadius={4}
                xs={3}
                height={'10em'}
                bgcolor={'#262626'}
                justifyContent={'center'}
                sx={{ boxShadow: 4 }}
            >
                <Grid item xs={12} justifyItems={'center'} maxHeight={'4em'}>
                    <h1 style={textStyle.titles}>Number of tasks:</h1>
                </Grid>
                <Grid container item xs={12} fontSize={50} justifyContent={'center'} color={'#f3f3f3'}>
                    {taskCount}
                </Grid>
            </Grid>
            <Grid container item border={1} borderRadius={4} xs={8} height={'10em'} bgcolor={'#262626'} sx={{ boxShadow: 4 }}>
                <Grid container item xs={4} border={1} justifyContent={'center'}>
                    <Grid item>Not started items</Grid>
                    <Grid container item xs={12} justifyContent={'center'}><h3>1</h3></Grid>
                </Grid>
                <Grid xs={4} border={1}>
                    1
                </Grid>
                <Grid xs={4} border={1}>
                    1
                </Grid>
            </Grid>
            <Grid item border={1} borderRadius={4} xs={8} height={'10em'} bgcolor={'#262626'} sx={{ boxShadow: 4 }}>
                
            </Grid>
            <Grid item border={1} borderRadius={4} xs={3} height={'10em'} bgcolor={'#262626'} sx={{ boxShadow: 4 }}>
                
            </Grid>
        </Grid>
    )
}

export default Dashboard;
