import { LeaderboardTwoTone } from '@mui/icons-material';
import { createSlice } from '@reduxjs/toolkit';

const addTaskSlice = createSlice({
  name: 'addTask',
  initialState: {
    showModal: false,
    priorityLevel: 0,
    titleFieldValue: '',
    descriptionFieldValue: '',
    allTasks: [],
    incompleteTasks: [],
    activeTasks: [],
    finishedTasks: []
  },
  reducers: {
    setShowModal: (state, action) => {
      state.showModal = action.payload;
    },
    setPriorityLevel: (state, action) => {
      state.priorityLevel = action.payload;
    },
    setTitleFieldValue: (state, action) => {
      state.titleFieldValue = action.payload;
    },
    setDescriptionFieldValue: (state, action) => {
      state.descriptionFieldValue = action.payload;
    },
    setNewTask: (state, action) => {
      state.incompleteTasks.push(action.payload);
      state.allTasks.push(action.payload);
    },
    moveTask: (state, action) => {
      let movedItem;

      switch (action.payload.start) {
        case 'incompleteTasks':
          movedItem = state.incompleteTasks[action.payload.source.index];
          state.incompleteTasks = state.incompleteTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'activeTasks':
          movedItem = state.activeTasks[action.payload.source.index];
          state.activeTasks = state.activeTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'finishedTasks':
          movedItem = state.finishedTasks[action.payload.source.index];
          state.finishedTasks = state.finishedTasks.filter((_, index) => index !== action.payload.source.index);
          break;
      }

      switch (action.payload.end) {
        case 'incompleteTasks':
          state.incompleteTasks.push(movedItem)
          break;
        case 'activeTasks':
          state.activeTasks.push(movedItem)
          break;
        case 'finishedTasks':
          state.finishedTasks.push(movedItem)
          break;
      }
    },
    reorderTasks: (state, action) => {
      let movedItem;
      let updatedSourceList;

      switch (action.payload.start) {
        case 'incompleteTasks':
          console.log('in reorderTasks')
          movedItem = state.incompleteTasks[action.payload.source.index];
          updatedSourceList = state.incompleteTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'activeTasks':
          movedItem = state.activeTasks[action.payload.source.index];
          updatedSourceList = state.activeTasks.filter((_, index) => index !== action.payload.source.index);
          break;
        case 'finishedTasks':
          movedItem = state.finishedTasks[action.payload.source.index];
          updatedSourceList = state.finishedTasks.filter((_, index) => index !== action.payload.source.index);
          break;
      }

      switch (action.payload.end) {
        case 'incompleteTasks':
          state.incompleteTasks = [state.incompleteTasks.slice(0, action.payload.destination.index), movedItem, state.incompleteTasks.slice(action.payload.destination.index)];
          break;
        case 'activeTasks':
          state.activeTasks = [state.activeTasks.slice(0, action.payload.destination.index), movedItem, state.activeTasks.slice(action.payload.destination.index)];
          break;
        case 'finishedTasks':
          state.finishedTasks = [state.finishedTasks.slice(0, action.payload.destination.index), movedItem, state.finishedTasks.slice(action.payload.destination.index)];
          break;
      }

      switch (action.payload.start) {
        case 'incompleteTasks':
          state.incompleteTasks = updatedSourceList
          break;
        case 'activeTasks':
          state.activeTasks = updatedSourceList
          break;
        case 'finishedTasks':
          state.finishedTask = updatedSourceList
          break;
      }
    }
  },
});

export const {
  setShowModal,
  setPriorityLevel,
  setTitleFieldValue,
  setDescriptionFieldValue,
  setNewTask,
  moveTask,
  reorderTasks
} = addTaskSlice.actions;

export default addTaskSlice.reducer;
