import { Grid } from "@mui/material";
import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { useSelector } from "react-redux";

const DayDragDrop = () => {
    const inCompleteTasks = useSelector(state => state.dayReducer.inCompleteTasks)
    const [secondDroppableItems, setSecondDroppableItems] = useState([]);

    const onDragEnd = (result) => {
        const { source, destination } = result;

        if (!destination) return; // If dragged outside droppable area

        // If moving within the same droppable
        if (source.droppableId === destination.droppableId) {
            const reorderedItems = [...items];
            const [removed] = reorderedItems.splice(source.index, 1);
            reorderedItems.splice(destination.index, 0, removed);
            setItems(reorderedItems);
        } else {
            // If moving from one droppable to another
            const updatedSourceItems = [...items];
            updatedSourceItems.splice(source.index, 1);
            setItems(updatedSourceItems);

            setSecondDroppableItems((prevItems) => {
                const updatedSecondDroppableItems = [...prevItems];
                updatedSecondDroppableItems.splice(destination.index, 0, items[source.index]);
                return updatedSecondDroppableItems;
            });
        }
    };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <Grid container columnGap={8} rowGap={2} marginTop={'1em'} justifyContent={'center'}>
                <Grid item xs={3.33} marginLeft={'1em'} fontSize={35}>
                    Not Started
                </Grid>
                <Grid item xs={3.33} fontSize={35}>
                    In Progress
                </Grid>
                <Grid item xs={3.33} fontSize={35}>
                    Completed
                </Grid>
                <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'} minHeight={500}>
                    <Droppable droppableId="droppable1">
                        {(provided) => (
                        <ul
                            {...provided.droppableProps}
                            ref={provided.innerRef}
                            style={{
                                listStyleType: 'none',
                                padding: '8px',
                                margin: '8px',
                                minHeight:'200px'
                            }}
                        >
                            {inCompleteTasks.map((item, index) => (
                                    <Draggable key={item.id} draggableId={String(item.id)} index={index}>
                                        {(provided, snapshot) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                            >
                                                {item.titleFieldValue}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                            {provided.placeholder}
                        </ul>
                        )}
                    </Droppable>
                </Grid>
                <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>
                    <Droppable droppableId="droppable2">
                        {(provided) => (
                        <ul
                            {...provided.droppableProps}
                            ref={provided.innerRef}
                            style={{
                                listStyleType: 'none',
                                padding: '8px',
                                margin: '8px',
                                minHeight:'200px'
                            }}
                        >
                            {provided.placeholder}
                        </ul>
                        )}
                    </Droppable>
                </Grid>
                <Grid item xs={3.33} border={[1, "dashed"]} borderRadius={'2em'}>

                </Grid>
            </Grid>
        </DragDropContext>
    )
}

export default DayDragDrop;
