import React, { useState } from "react";
import { Box, Button, FormControl, Grid, InputLabel, MenuItem, Modal, Select, TextField } from "@mui/material";
import CloseIcon from '@mui/icons-material/Close';
import PropTypes from 'prop-types';
import { useDispatch } from "react-redux";
import { setShowModal } from "../../Reducers/DayReducer";

const DayModal = (props) => {
    const dispatch = useDispatch();
    const [priorityValue, setPriorityValue] = useState(0);

    const closeModal = () => {
        dispatch(setShowModal(false));
    };

    const publishTask = (title, description, priority) => {
        dispatch(publishTask(title, description, priority))
    }

    const prioritySelect = (e) => {
        e.preventDefault();
        setPriorityValue(e.target.value)
        dispatch()
    }

    return (
        <Modal
            open={props.showModal}
            onClose={closeModal}
        >
            <Box
                minHeight={'20em'}
                width={'30em'}
                justifyContent={'center'}
                border={1}
                borderRadius={3}
                sx={{
                    position: 'absolute',
                    top: '30%',
                    left: '33%',
                    bgcolor: '#1c1c1c'
                    }}
            >
                <Grid container>
                    <Grid item xs={6}>
                        <h1 style={{color: '#f5f5f5', marginLeft: '0.5em'}}>{props.title}</h1>
                    </Grid>
                    <Grid item xs={6}>
                        <Button onClick={() => {dispatch(closeModal)}} sx={{position: 'absolute', top: 0, right: 0, borderRadius: 3}}>
                            <CloseIcon />
                        </Button>
                    </Grid>
                    <Grid container rowGap={2}>
                        <Grid item marginLeft={'1em'} xs={7}>
                            <TextField id={'titleField'} variant='standard' label='Title' />
                        </Grid>
                        <Grid item marginLeft={'1em'} xs={3}>
                            <FormControl fullWidth>
                                <InputLabel>Priorty</InputLabel>
                                <Select
                                    label="Priority"
                                    variant='outlined'
                                    defaultValue={0}
                                    onChange={prioritySelect}
                                >
                                    <MenuItem value={0}>None</MenuItem>
                                    <MenuItem value={1}>Low</MenuItem>
                                    <MenuItem value={2}>Medium</MenuItem>
                                    <MenuItem value={3}>High</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item marginLeft={'1em'} xs={6}>
                            <TextField id={'descriptionField'} variant='standard' label='Description' multiline fullWidth />
                        </Grid>
                        <Grid item xs={5}>
                            <Button
                                variant='contained'
                                sx={{
                                    position:'absolute',
                                    right: 20,
                                    bottom: 20
                                }}
                                onClick={() => {dispatch(publishTask)}}
                            >
                                Publish
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>
            </Box>
        </Modal>
    )
};

DayModal.propTypes = {
    showModal: PropTypes.bool,
    publish: PropTypes.func
}

export default DayModal;
